require 'rho/rhocontroller'
require 'helpers/browser_helper'

class InventoryController < Rho::RhoController
  include BrowserHelper
  
  def scanPage
    # receive an integer as parameter, which represent the number of the scanner we wish to use
    #barcode_reader = $barcode_readers[@params["scannerIndex"].to_i]
    Rho::Barcode.enable({}, url_for(:action => :scan_received_callback))
    render :action => :scanPage
  end
  
 def scan_received_callback
   barcode = @params['data']
   puts "Barcode: ${barcode}"
   product = ProductCatalog.find(
   :first,
   :conditions => {:SKU => barcode}
   )
   if product.nil?
     product = ProductCatalog.create({"SKU" => barcode, "Name" => "Unknown Product", "Image" => "data:image/jpeg;base64,%3D"})
       end
   
       puts "Product: ${product}"
   name = product.Name
   if product.Image
     WebView.execute_js("setImgSrc('"+product.Image+"'); ")
       end
   
       inventoryItem = Inventory.find(
       :first,
       :conditions => {:SKU => barcode}
       )
   
       if inventoryItem.nil?
         inventoryItem = Inventory.create(
         {"SKU" => barcode, "Quantity" => 1}
     )
   else
     itemCount = inventoryItem.Quantity.to_i
     itemCount += 1
     inventoryItem.update_attributes(
     {"Quantity" => itemCount.to_s}
     )
   end
   inventoryItem.save
   if itemCount.nil?
     itemCount = 1
   end
   getInventoryCount
   WebView.execute_js("setProductData('"+name+"','"+barcode+"','"+itemCount.to_s+"'); ")
   
     end


  def getInventoryCount

    items= Inventory.find(
    :all
    )
    scanCount = 0

    items.each do
      |item|
      scanCount = scanCount + item.Quantity.to_i()
    end
    WebView.execute_js("setProductCount('"+scanCount.to_s+"'); ")
  end

 # GET /Inventory
  def index
    @inventories = Inventory.find(:all)
    render :back => '/app'
  end

  # GET /Inventory/{1}
  def show
    @inventory = Inventory.find(@params['id'])
    if @inventory
      render :action => :show, :back => url_for(:action => :index)
    else
      redirect :action => :index
    end
  end

  # GET /Inventory/new
  def new
    @inventory = Inventory.new
    render :action => :new, :back => url_for(:action => :index)
  end

  # GET /Inventory/{1}/edit
  def edit
    @inventory = Inventory.find(@params['id'])
    if @inventory
      render :action => :edit, :back => url_for(:action => :index)
    else
      redirect :action => :index
    end
  end

  # POST /Inventory/create
  def create
    @inventory = Inventory.create(@params['inventory'])
    redirect :action => :index
  end

  # POST /Inventory/{1}/update
  def update
    @inventory = Inventory.find(@params['id'])
    @inventory.update_attributes(@params['inventory']) if @inventory
    redirect :action => :index
  end

  # POST /Inventory/{1}/delete
  def delete
    @inventory = Inventory.find(@params['id'])
    @inventory.destroy if @inventory
    redirect :action => :index  
  end
end
